import React, { useState } from "react";

export default function Textform(props) {
  const UpperCase = () => {
    console.log("UpperCase Clicked");
    let UpperCase = text.toUpperCase();
    setText(UpperCase);
  };
  const LowerCase = () => {
    console.log("LowerCase Clicked");
    let LowerCase = text.toLowerCase();
    setText(LowerCase);
  };
 const capitalizedText=()=>{
  console.log("Text Capitalized");
  let capitalizedtext=text.split(" ");
  let text1="";
  for(let item=0;item<capitalizedtext.length;item++){
    if(item===capitalizedtext.length-1)
    {
      text1  +=  (capitalizedtext[item].charAt(0).toUpperCase() + capitalizedtext[item].substring(1));
    }
    else{
    text1   +=  (capitalizedtext[item].charAt(0).toUpperCase() + capitalizedtext[item].substring(1) + " ");
    }
  }
  setText(text1);

 }
 const clearText=()=>{
  console.log("Text Cleared");
  let newtext='';
  setText(newtext);
 }
 const copyText=()=>{
  console.log("Text Copied");
  alert('Text Copied')
  navigator.clipboard.writeText(text);
 }
 const downloadText=(event)=>{
  console.log("Text Downloaded");
  let el=document.getElementById("id")
  event.href = URL.createObjectURL(text);
   text.download = "myFile.txt";

 };
  const handleonchange = (event) => {
    console.log("Onchange");
    setText(event.target.value);
  };
  const [text, setText] = useState("");            //useState Hook too change the state of component,text is state variable and setText is a function.

  return (
    <>
      <div>
        <div className="mb-3">
          <h1>{props.heading}</h1>

          <textarea
            className="form-control"
            id="text"
            rows="5" placeholder="Enter Text Here"
            value={text}
            onChange={handleonchange}
          ></textarea>
        </div>
        <button className="btn btn-primary mx-2 btn-danger " onClick={UpperCase}>
          to UpperCase
        </button>
        <button className="btn btn-primary mx-2 btn-success" onClick={LowerCase}>
          to LowerCase
        </button>
        <button className="btn btn-primary mx-2 btn-success" onClick={capitalizedText}>
          Text Capitalized
        </button>
        <button className="btn btn-primary mx-2 btn-dark" onClick={clearText}>
          Clear Text
        </button>
        <button className="btn btn-primary mx-2 btn-dark" onClick={copyText}>
          Copy Text
        </button>
        <button className="btn btn-primary mx-2 btn-dark" onClick={downloadText}>
          Download Text
        </button>
      </div>
      <div className="container my-4">
        <h1 >Your Text Summary</h1>
        <p>No. of Words : {text.split(" ").length - 1}</p>
        <p>No. of Characters : {text.length}</p>
        <p>No. of words read in per minute : {0.008*text.split(" ").length}</p>

        <h3>Preview</h3>
        <p>{text}</p>
      </div>
    </>
  );
}
