import React, { useState } from "react";

export default function Textform(props) {
  const UpperCase = () => {
    console.log("UpperCase Clicked");
    let UpperCase = text.toUpperCase();
    setText(UpperCase);
  };
  const LowerCase = () => {
    console.log("LowerCase Clicked");
    let LowerCase = text.toLowerCase();
    setText(LowerCase);
  };
  const handleonchange = (event) => {
    console.log("Onchange");
    setText(event.target.value);
  };
  const [text, setText] = useState("");            //useState Hook too change the state of component,text is state variable and setText is a function.

  return (
    <>
      <div>
        <div className="mb-3">
          <h1>{props.heading}</h1>

          <textarea
            className="form-control"
            id="text"
            rows="5" placeholder="Enter Text Here"
            value={text}
            onChange={handleonchange}
          ></textarea>
        </div>
        <button className="btn btn-primary mx-2 btn-outline-danger" onClick={UpperCase}>
          to UpperCase
        </button>
        <button className="btn btn-primary mx-2 btn-outline-success" onClick={LowerCase}>
          to LowerCase
        </button>
      </div>
      <div className="container my-4">
        <h1 >Your Text Summary</h1>
        <p>No. of Words : {text.split(" ").length}</p>
        <p>No. of Characters : {text.length}</p>
        <h3>Preview</h3>
        <p>{text}</p>
      </div>
    </>
  );
}
