import "./App.css";
import Navbar from "./Components/Navbar";

// import About from "./Components/About";
import TextForm from "./Components/TextForm";
import React, { useState } from "react";
import Alert from "./Components/Alert";

function App() {
  const [btntext, setbtntext] = useState("Enable Dark Mode");

  const [mode,setMode]=useState("light");
  const togglemode=()=>{
    if(mode==="light"){
      setMode("dark");
      document.body.style.backgroundColor="#343a40";
      setbtntext("Enable Light Mode");
      showalert("Dark Mode Enabled","success");
      setTimeout(() => {
        setalert(null);

      },1500);

    }
    else{
      setMode("light");
      document.body.style.backgroundColor="white";
      setbtntext("Enable Dark Mode");
      showalert("Light Mode Enabled","success");
      setTimeout(() => {
        setalert(null);

      },1500);

    }
  }
  const [alert, setalert] = useState(null);
  const showalert=(message,type)=>{
setalert({msg:message,
type:type
  })
  }
  const icon=<i class="fa-solid fa-magnifying-glass"></i>
  return (
    <>
    <div >
      <Navbar title="Text Inspector + ${icon}" mode={mode} togglemode={togglemode} btntext={btntext}/> 
      <Alert alert={alert}/>
      <div className="container  my-3">
         <TextForm heading="Enter the Text to Modify" mode={mode} showalert={showalert} setalert={setalert}/> 

      </div>
      </div>
    </>
  );
}

export default App;
