import React, { useState } from "react";

function App() {
  const [mode,setMode]=useState("light");
  const tooglemode=()=>{
    if(mode==="light"){
      setMode("Dark")
    }
  }
  return (
    <>
    <div >
      <Navbar title="Text Analyzer" />
      <Heading heading="Welcome to My First React App" />
      <div className="container  my-3">
         <TextForm heading="Enter the Text to Modify" /> 

      </div>
      </div>
    </>
  );
}

export default App;
