import React, { useState } from "react";

export default function Textform(props) {
  const UpperCase = () => {
    if(text===""){
      props.showalert("No Text to Coverted In UpperCase","info");
      setTimeout(() => {
        props.setalert(null);
        
      },1500);}
      else{
        console.log("UpperCase Clicked");
    let UpperCase = text.toUpperCase();
    setText(UpperCase);
    props.showalert("Text Converted To UpperCase","success");
    setTimeout(() => {
      props.setalert(null);
      
    },1500);
  }
  };
  const LowerCase = () => {
    if(text===""){
      props.showalert("No Text to Converted To Lowercase","info");
      setTimeout(() => {
        props.setalert(null);
        
      },1500);
    }
      else{
    console.log("LowerCase Clicked");
    let LowerCase = text.toLowerCase();
    setText(LowerCase);
    props.showalert("Text Converted in LowerCase","success");
    setTimeout(() => {
      props.setalert(null);
      
    },1500);
  }
  };
  const capitalizedText = () => {
    if(text===""){
      props.showalert("No Text to be Capitalized","info");
      setTimeout(() => {
        props.setalert(null);
        
      },1500);
    }
    else{
    console.log("Text Capitalized");
    let capitalizedtext = text.split(" ");
    let text1 = "";
    for (let item = 0; item < capitalizedtext.length; item++) {
      if (item === capitalizedtext.length - 1) {
        text1 +=
          capitalizedtext[item].charAt(0).toUpperCase() +
          capitalizedtext[item].substring(1);
      } else {
        text1 +=
          capitalizedtext[item].charAt(0).toUpperCase() +
          capitalizedtext[item].substring(1) +
          " ";
      }
    }
    setText(text1);
    props.showalert("Text Capitalized","success");
    setTimeout(() => {
      props.setalert(null);
      
    },1500);
  }
  };
  const clearText = () => {
    console.log("Text Cleared");
    if(text===""){
      props.showalert("No Text to Clear","info");
      setTimeout(() => {
        props.setalert(null);
        
      },1500);
    }
    else{
    let newtext = "";
    setText(newtext);
    props.showalert("Text is Cleared","success");
    setTimeout(() => {
      props.setalert(null);
      
    },1500);
  }
  };
  const copyText = () => {
    if(text===""){
    
      props.showalert("Enter Some Text Then Copied","warning");
      
    }
    
    else{
     
    console.log("Text Copied");
    navigator.clipboard.writeText(text);
    props.showalert("Text Copied to ClipBoard","success");
    setTimeout(() => {
      props.setalert(null);
      
    },1500);
  }
  };
 const  downloadTxtFile = () => {
  if(text===""){
    props.showalert("Enter Some Text then Download it","warning");
    setTimeout(() => {
      props.setalert(null);
      
    },1500);
  }
  else{
    const element = document.createElement("a");
    const file = new Blob([text],    
                {type: 'text/plain;charset=utf-8'});
    element.href = URL.createObjectURL(file);
    element.download = "yourFile.txt";
    document.body.appendChild(element);
    element.click();
    props.showalert("Text  Downloaded","success");
    setTimeout(() => {
      props.setalert(null);
      
    },500);
  }
  }
  const removeextraSpaces= () => {
    
    if(text===""){
      props.showalert("No Text to ","info");
      setTimeout(() => {
        props.setalert(null);
        
      },1500);
    }
      else{
    let extraspaces=text.split(/[ ]+/);
    setText(extraspaces.join(" "));
    props.showalert("Extra Space Removed From Text","success");
    setTimeout(() => {
      props.setalert(null);
      
    },1500);
  }

   
  };
  const handleonchange = (event) => {
    console.log("Onchange");
    setText(event.target.value);
  };
  const [text, setText] = useState(""); //useState Hook too change the state of component,text is state variable and setText is a function.
  // const[mystyle,settextareastyle]=useState(color:'white',
  //   backgroundColor:'black'

  // );
  // const [mystyle, setmystyle] = useState({
  //   color: "black",
  //   backgroundColor: "white",
  // });

  // const [btntext, setbtntext] = useState("Enable Dark Mode");

  // const toogledarkmode = () => {
  //   if (mystyle.color === "black") {
  //     setmystyle({
  //       color: "white",
  //       backgroundColor: "black",
  //     });
  //     setbtntext("Enable Light Mode");
  //   } else {
  //     setmystyle({
  //       color: "black",
  //       backgroundColor: "white",
  //     });
  //     setbtntext("Enable Dark Mode");
  //   }
  // };
  return (
    <>
      <div class={`container border border-${props.mode==="dark"?"white":"dark"} bg-${props.mode} `} >
     
       
          
            <h1 style={{color:props.mode==="dark"?"white":"black"}}>{props.heading}</h1>

            <textarea
              className="form-control"
              id="text"
              rows="5"
              placeholder="Enter Text Here"
              value={text}
              onChange={handleonchange}
              name="maintextarea"
            ></textarea>
          
          <button
            className={`btn btn-primary mx-2 btn-success my-3 border border-${props.mode==="dark"?"white":"dark"} `}
            onClick={UpperCase}
            style={{color:props.mode==="dark"?"white":"black",backgroundolor:props.mode==="dark"?"black":"white"}}
          >
            UPPERCASE
          </button>
          <button
            className={`btn btn-primary mx-2 btn-blue my-3 border border-${props.mode==="dark"?"white":"dark"} `}
            onClick={LowerCase}
            style={{color:props.mode==="dark"?"white":"black",backgroundolor:props.mode==="dark"?"black":"white"}}
          >
            lowercase
          </button>
          <button
            className={`btn btn-primary mx-2 btn-warning my-3 border border-${props.mode==="dark"?"white":"dark"} `}
            onClick={capitalizedText}
            style={{color:props.mode==="dark"?"white":"black",backgroundolor:props.mode==="dark"?"black":"white"}}
          >
            Text Capitalized
          </button>
          <button
            className={`btn btn-primary mx-2 btn-info my-3 border border-${props.mode==="dark"?"white":"dark"} `}
            onClick={removeextraSpaces}
            style={{color:props.mode==="dark"?"white":"black",backgroundolor:props.mode==="dark"?"black":"white"}}
          >
            Remove Extra Space
          </button>
          <button
            className={`btn btn-primary mx-2 btn-secondary my-3 border border-${props.mode==="dark"?"white":"dark"} `}
            onClick={clearText}
            style={{color:props.mode==="dark"?"white":"black",backgroundolor:props.mode==="dark"?"black":"white"}}
          >
            Clear Text
          </button>
          <button
            className={`btn btn-primary mx-2 btn-link my-3 border border-${props.mode==="dark"?"white":"dark"} `}
            onClick={copyText}
            style={{color:props.mode==="dark"?"white":"black",backgroundolor:props.mode==="dark"?"black":"white"}}
            title="Copy Text"
          >
            <i class="fa-solid fa-copy"></i>Copy Text
          </button>
          <button
            className={`btn btn-primary mx-2 btn-primary my-3 border border-${props.mode==="dark"?"white":"dark"} `}
            onClick={downloadTxtFile}
            style={{color:props.mode==="dark"?"white":"black",backgroundolor:props.mode==="dark"?"black":"white"}}
            title="Download Text" id="input"
          >
            <i class="fa-solid fa-circle-down"></i>Download Text
          </button>
   
        
        <div className={`container my-4 border border-${props.mode==="dark"?"white":"dark"} bg-${props.mode} `} style={{color:props.mode==="dark"?"white":"black"}} >
          <h1>Your Text Summary</h1>
          <hr className="bg-dark"/>
          <p>No. of Words : {text.split(" ").length - 1}</p>
          <p>No. of Characters : {text.length}</p>
          <p>
            No. of words read in per minute : {0.008 * text.split(" ").length}
          </p>
          <hr className="bg-dark"/>

          <h3>Preview Your Text</h3>
          
          <textarea
              className="form-control my-2"
              id="text"
              rows="5"
              name="textarea"  
              value={text.length===0?"Enter Some Text  Above to Preview it Here":text}
            ></textarea>
         
        </div>
        
        
      </div>
    </>
  );
}
